# Making the server listen on non priviliged ports would allow the container to run with less privileges, even with a RO root fileysstem.

server {
    # TDOD: Change to non priviliged port, 8080, and use portmapping in docker (compose) or kubernetes services to 
    # be reachable via port 80
    listen 80;
    server_name _;

    return 301 https://$host$request_uri;
}


server {
    # TDOD: Change to non priviliged port, 8443, and use portmapping in docker (compose) or kubernetes services to 
    # be reachable via port 443
    listen 443 ssl;
    server_name _;

    ssl_certificate         /run/secrets/cert;
    ssl_certificate_key     /run/secrets/key;
    ssl_session_timeout 5m;

    location / {
        root /app/;
        index index.html;
        try_files $uri $uri /index.html;
    }
}

