import { Theme } from '@mui/material'
import { ThemeProvider } from '@mui/material/styles'
import { QueryClient, QueryClientProvider } from '@tanstack/react-query'
import { SnackbarProvider } from 'notistack'
import { ReactElement, useMemo } from 'react'
import { Provider } from 'react-redux'
import { PersistGate } from 'redux-persist/integration/react'
import { CustomMatomoInstance } from 'src/addons'
import PermissionsWrapper from 'src/components/Permissions/PermissionsWrapper'
import { LOCAL_STORAGE_THEME_KEY } from 'src/components/UserSettings/UserSettingsAppearance'
import MatomoProvider from 'src/matomo/react/MatomoProvider'
import { useAppDispatch, useAppSelector } from 'src/redux/hooks'
import { persistor, store } from 'src/redux/store'
import { themes } from 'src/theme/Theme'
import ThemedMapWrapper from './components/Map/ThemedMapWrapper'
import Loader from './components/shared/Loader/Loader'
import useAuthenticationQuery from './hooks/useAuthenticationQuery'
import RootRouter from './routes'
import { loadPreferences } from './redux/slices/preferences/loadPreferences'

// inform Redux store about locationChange
const SBProvider = SnackbarProvider as unknown as (props: { maxSnack: number; children: ReactElement }) => ReactElement

const AppWithStore = () => {
    const dispatch = useAppDispatch()
    const { isLoading } = useAuthenticationQuery()

    const themeKeyFromStore = useAppSelector(state => state.preferences.private['setting.themeMode'])
    const themeKey = themeKeyFromStore || localStorage.getItem(LOCAL_STORAGE_THEME_KEY) || 'light'
    const theme = useMemo(() => themes.find(t => t.label === themeKey), [themeKey])

    if (isLoading) {
        return <Loader />
    }

    dispatch(loadPreferences())

    return (
        <ThemeProvider theme={theme as Theme}>
            <ThemedMapWrapper mode={themeKey}>
                <PermissionsWrapper>
                    <RootRouter />
                </PermissionsWrapper>
            </ThemedMapWrapper>
        </ThemeProvider>
    )
}

const App = () => {
    const queryClient = new QueryClient({ defaultOptions: { queries: { networkMode: 'offlineFirst' } } })

    const renderer = (
        <SBProvider maxSnack={3}>
            <Provider store={store}>
                <QueryClientProvider client={queryClient}>
                    <PersistGate loading={null} persistor={persistor}>
                        <AppWithStore />
                    </PersistGate>
                </QueryClientProvider>
            </Provider>
        </SBProvider>
    )
    if (!CustomMatomoInstance) {
        return renderer
    }
    return <MatomoProvider value={CustomMatomoInstance}>{renderer}</MatomoProvider>
}

export default App

