import { useParams } from 'react-router-dom'
import { GenericTraceList } from 'src/components/TraceList/GenericTraceList'
import Layout from 'src/layout'
import NotFound from 'src/pages/not-found/NotFoundPage'
import { usePages } from '../usePages'
import useGenericPages from './useGenericPages'

const GenericLayoutPage = () => {
    const params = useParams()

    const lastSeparator = params['*']?.lastIndexOf('/')
    const pageId = !lastSeparator || lastSeparator === -1 ? params['*'] : params['*']?.slice(lastSeparator + 1)

    const pages = usePages()
    // Pages that have no complex functionality
    const genericPages = useGenericPages()

    const page = pages.find(pageType => pageType.id === pageId)
    const genericPageProps = genericPages.find(genericPage => genericPage.id === pageId)

    const layoutProps = {
        additionalBreadcrumbs: page?.additionalBreadcrumbs ?? genericPageProps?.additionalBreadcrumbs ?? [],
    }

    if (genericPageProps) {
        return (
            <Layout {...layoutProps}>
                <GenericTraceList {...genericPageProps} />
            </Layout>
        )
    }

    if (!page) return <NotFound />

    return <Layout {...layoutProps}>{page.component}</Layout>
}

export default GenericLayoutPage

import { Theme } from '@mui/material'
import { ThemeProvider } from '@mui/material/styles'
import { QueryClient, QueryClientProvider } from '@tanstack/react-query'
import { SnackbarProvider } from 'notistack'
import { ReactElement, useMemo } from 'react'
import { Provider } from 'react-redux'
import { PersistGate } from 'redux-persist/integration/react'
import { CustomMatomoInstance } from 'src/addons'
import PermissionsWrapper from 'src/components/Permissions/PermissionsWrapper'
import { LOCAL_STORAGE_THEME_KEY } from 'src/components/UserSettings/UserSettingsAppearance'
import MatomoProvider from 'src/matomo/react/MatomoProvider'
import { useAppDispatch, useAppSelector } from 'src/redux/hooks'
import { persistor, store } from 'src/redux/store'
import { themes } from 'src/theme/Theme'
import ThemedMapWrapper from './components/Map/ThemedMapWrapper'
import Loader from './components/shared/Loader/Loader'
import useAuthenticationQuery from './hooks/useAuthenticationQuery'
import RootRouter from './routes'
import { loadPreferences } from './redux/slices/preferences/loadPreferences'

// inform Redux store about locationChange
const SBProvider = SnackbarProvider as unknown as (props: { maxSnack: number; children: ReactElement }) => ReactElement

const AppWithStore = () => {
    const dispatch = useAppDispatch()
    const { isLoading } = useAuthenticationQuery()

    const themeKeyFromStore = useAppSelector(state => state.preferences.private['setting.themeMode'])
    const themeKey = themeKeyFromStore || localStorage.getItem(LOCAL_STORAGE_THEME_KEY) || 'light'
    const theme = useMemo(() => themes.find(t => t.label === themeKey), [themeKey])

    if (isLoading) {
        return <Loader />
    }

    dispatch(loadPreferences())

    return (
        <ThemeProvider theme={theme as Theme}>
            <ThemedMapWrapper mode={themeKey}>
                <PermissionsWrapper>
                    <RootRouter />
                </PermissionsWrapper>
            </ThemedMapWrapper>
        </ThemeProvider>
    )
}

const App = () => {
    const queryClient = new QueryClient({ defaultOptions: { queries: { networkMode: 'offlineFirst' } } })

    const renderer = (
        <SBProvider maxSnack={3}>
            <Provider store={store}>
                <QueryClientProvider client={queryClient}>
                    <PersistGate loading={null} persistor={persistor}>
                        <AppWithStore />
                    </PersistGate>
                </QueryClientProvider>
            </Provider>
        </SBProvider>
    )
    if (!CustomMatomoInstance) {
        return renderer
    }
    return <MatomoProvider value={CustomMatomoInstance}>{renderer}</MatomoProvider>
}

export default App

import { lazy, Suspense } from 'react'
import { Route, BrowserRouter as Router, Routes } from 'react-router-dom'
import { AddonRouters } from 'src/addons'
import CaseLoaderGate from 'src/components/CaseOverview/CaseLoaderGate'
import Loader from 'src/components/shared/Loader/Loader'
import { NON_STATIC_URLS, PAGES } from 'src/constants/pages'
import ContactsPage from 'src/pages/communications/contacts/ContactsPage'
import GlobalNotificationPage from 'src/pages/global-notification/GlobalNotificationPage'

const GenericLayoutPage = lazy(() => import('src/pages/generic-pages/GenericLayoutPage'))
const FileExplorerPage = lazy(() => import('src/pages/file-explorer/FileExplorerPage'))
const SearchPage = lazy(() => import('src/pages/search/SearchPage'))
const LocationsPage = lazy(() => import('src/pages/locations/LocationsPage'))
const Dashboard = lazy(() => import('src/pages/Dashboard'))
const NotFound = lazy(() => import('src/pages/not-found/NotFoundPage'))
const Admin = lazy(() => import('src/pages/admin/AdminPage'))
const Addon = lazy(() => import('src/pages/addon/AddonPage'))
const AddonTools = lazy(() => import('src/pages/addon-tools/AddonToolsPage'))
const CasesPage = lazy(() => import('src/pages/cases/CasesPage'))
const Report = lazy(() => import('src/pages/report/ReportPage'))
const Reports = lazy(() => import('src/pages/reports/Reports'))
const CasePage = lazy(() => import('src/pages/case/CasePage'))
const ConversationsPage = lazy(() => import('src/pages/communications/conversations/ConversationsPage'))
const TasksPage = lazy(() => import('src/pages/tasks/Tasks'))

// inform Redux store about locationChange
const AppRouter = () => (
    <Router>
        <Suspense fallback={<Loader />}>
            <Routes>
                <Route path={PAGES.DASHBOARD} element={<Dashboard />} />
                <Route path={PAGES.ADMIN} element={<Admin />} />
                <Route path={PAGES.ADDON} element={<Addon />} />
                <Route path={PAGES.TOOL_GLOBAL_NOTIFICATION} element={<GlobalNotificationPage />} />
                <Route path={PAGES.TOOL_ADDON} element={<AddonTools />} />
                <Route path={PAGES.TOOL_ADDON_CASE} element={<AddonTools />} />
                <Route path={PAGES.TOOL_ADDON_NAME} element={<AddonTools />} />
                <Route path={PAGES.TOOL_ADDON_EXTENSION} element={<AddonTools />} />
                <Route path={PAGES.TASKS} element={<TasksPage />} />
                <Route path={PAGES.CASES} element={<CasesPage />} />
                <Route path={PAGES.CASE} element={<CaseLoaderGate />}>
                    <Route path={PAGES.CASE} element={<CasePage />} />
                    <Route path={NON_STATIC_URLS.SEARCH()} element={<SearchPage />} />
                    <Route path={NON_STATIC_URLS.FILE_EXPLORER()} element={<FileExplorerPage />} />
                    <Route path={NON_STATIC_URLS.COMMUNICATIONS('contacts')} element={<ContactsPage />} />
                    <Route path={NON_STATIC_URLS.COMMUNICATIONS('conversations')} element={<ConversationsPage />} />
                    <Route path={NON_STATIC_URLS.LOCATIONS()} element={<LocationsPage />} />
                    <Route path={PAGES.REPORT} element={<Report />} />
                    <Route path={PAGES.REPORTS} element={<Reports />} />
                    <Route path={PAGES.GENERIC_PAGE} element={<GenericLayoutPage />} />
                    {AddonRouters.withCase.map((item, i) => (
                        <Route key={i} path={item.path} element={item.element} />
                    ))}
                </Route>

                {AddonRouters.withoutCase.map((item, i) => (
                    <Route key={i} path={item.path} element={item.element} />
                ))}
                <Route element={<NotFound />} />
            </Routes>
        </Suspense>
    </Router>
)

export default AppRouter

