export type AddonComponentType = UIAddon | AdminAddon

export type AdminAddonsDefinitionsType = { label: string; component: AdminAddon; icon: IconDefinition }
export type CaseAddonType = {
    comments?: string
    target?: string
    department?: string
}

export type AddonRouteProps = {
    path: string
    element: ReactNode
}

export const parseAddonRoute = (
    ar: AddonRouteProps[]
): { withCase: AddonRouteProps[]; withoutCase: AddonRouteProps[] } =>
    ar.reduce(
        (acc, value) => {
            if (value.path.includes('/case/:caseId/')) {
                return { ...acc, withCase: [...acc.withCase, value] }
            }
            return { ...acc, withoutCase: [...acc.withoutCase, value] }
        },
        { withCase: [] as AddonRouteProps[], withoutCase: [] as AddonRouteProps[] }
    )

export const AddonRouters: { withCase: AddonRouteProps[]; withoutCase: AddonRouteProps[] } = parseAddonRoute([])

