import React, { useState, useEffect } from 'react';
import './InventarioList.css';

const InventarioList = () => {
  const [articulos, setArticulos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadInventario = async () => {
      try {
        const response = await fetch('/inventario.xml');
        if (!response.ok) {
          throw new Error('Error al cargar el archivo XML');
        }

        const xmlText = await response.text();
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlText, 'text/xml');

        const articulosElements = xmlDoc.getElementsByTagName('articulo');
        const articulosData = [];

        for (let i = 0; i < articulosElements.length; i++) {
          const articulo = articulosElements[i];
          articulosData.push({
            id: articulo.getElementsByTagName('id')[0].textContent,
            marca: articulo.getElementsByTagName('marca')[0].textContent,
            modelo: articulo.getElementsByTagName('modelo')[0].textContent,
            numeroSerie: articulo.getElementsByTagName('numeroSerie')[0].textContent
          });
        }

        setArticulos(articulosData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    loadInventario();
  }, []);

  if (loading) return <div className="loading">Cargando inventario...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="inventario-container">
      <h1>Inventario de Artículos</h1>
      <div className="inventario-grid">
        {articulos.map((articulo) => (
          <div key={articulo.id} className="articulo-card">
            <h3>ID: {articulo.id}</h3>
            <p><strong>Marca:</strong> {articulo.marca}</p>
            <p><strong>Modelo:</strong> {articulo.modelo}</p>
            <p><strong>Nº Serie:</strong> {articulo.numeroSerie}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default InventarioList;

