  const [currentView, setCurrentView] = useState(null);

  const handleMenuSelect = (menuId, menuLabel) => {
    console.log(`Menú seleccionado: ${menuId} - ${menuLabel}`);

    if (menuId === 'inventario' && menuLabel === 'Listado Inventario') {
      setCurrentView('inventario-list');
