import { useState } from 'react';
import './App.css';
import InventarioList from './components/InventarioList';
import MenuHorizontal from './components/MenuHorizontal';

function App() {
  const [currentView, setCurrentView] = useState(null);

  const handleMenuSelect = (menuId, menuLabel) => {
    console.log(`Menú seleccionado: ${menuId} - ${menuLabel}`);

    if (menuId === 'inventario' && menuLabel === 'Listado Inventario') {
      setCurrentView('inventario-list');
    } else {
      setCurrentView(null);
    }
  };

  const renderContent = () => {
    switch (currentView) {
      case 'inventario-list':
        return <InventarioList />;
      default:
        return (
          <div className="welcome-screen">
            <h2>Bienvenido a data4n6</h2>
            <p>Selecciona una opción del menú para comenzar</p>
          </div>
        );
    }
  };

  return (
    <div className="App">
      <MenuHorizontal onMenuSelect={handleMenuSelect} />
      {renderContent()}
    </div>
  );
}

export default App;

