import './MenuHorizontal.css';

interface MenuItem {
  label: string;
  id: string;
  submenus: string[];
}

interface MenuHorizontalProps {
  onMenuSelect?: (menuId: string, menuLabel: string) => void;
}

const MenuHorizontal: React.FC<MenuHorizontalProps> = ({ onMenuSelect }) => {
  const [activeMenu, setActiveMenu] = useState<string>('Inventario');

  const menuItems: MenuItem[] = [
    {
      label: 'Aplicación',
      id: 'aplicacion',
      submenus: ['Configuración', 'Usuarios', 'Permisos']
    },
    {
      label: 'Administración',
      id: 'administracion',
      submenus: ['Gestión', 'Reportes', 'Logs']
    },
    {
      label: 'data4n6',
      id: 'data4n6',
      submenus: ['Dashboard', 'Análisis', 'Configuración']
    },
    {
      label: 'Inventario',
      id: 'inventario',
      submenus: ['Listado Inventario', 'Agregar Artículo', 'Buscar']
    },
    {
      label: 'Herramientas',
      id: 'herramientas',
      submenus: ['Exportar', 'Importar', 'Backup']
    }
  ];

  const handleMenuClick = (menuId: string, menuLabel: string): void => {
    setActiveMenu(menuLabel);
    if (onMenuSelect) {
      onMenuSelect(menuId, menuLabel);
    }
  };

  const handleSubmenuClick = (parentId: string, submenuLabel: string): void => {
    if (onMenuSelect) {
      onMenuSelect(parentId, submenuLabel);
    }
  };

  return (
    <nav className="menu-horizontal">
      <div className="menu-container">
        {menuItems.map((item) => (
          <div key={item.id} className="menu-item">
            <button
              className={`menu-button ${activeMenu === item.label ? 'active' : ''}`}
              onClick={() => handleMenuClick(item.id, item.label)}
            >
              {item.label}
            </button>
            <div className="submenu">
              {item.submenus.map((submenu, index) => (
                <button
                  key={index}
                  className="submenu-item"
                  onClick={() => handleSubmenuClick(item.id, submenu)}
                >
                  {submenu}
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>
    </nav>
  );
};

