import React, { useState } from 'react';
import { Menu, Typography } from 'antd';
import type { MenuProps } from 'antd';
import {
  AppstoreOutlined,
  SettingOutlined,
  DatabaseOutlined,
  InboxOutlined,
  ToolOutlined,
} from '@ant-design/icons';

const { Title } = Typography;

interface MenuHorizontalProps {
  onMenuSelect?: (menuId: string, menuLabel: string) => void;
}

const MenuHorizontal: React.FC<MenuHorizontalProps> = ({ onMenuSelect }) => {
  const [current, setCurrent] = useState<string>('inventario');

  const items: MenuProps['items'] = [
    {
      label: 'Aplicación',
      key: 'aplicacion',
      icon: <AppstoreOutlined />,
      children: [
        { key: 'aplicacion-config', label: 'Configuración' },
        { key: 'aplicacion-users', label: 'Usuarios' },
        { key: 'aplicacion-permisos', label: 'Permisos' },
      ],
    },
    {
      label: 'Administración',
      key: 'administracion',
      icon: <SettingOutlined />,
      children: [
        { key: 'administracion-gestion', label: 'Gestión' },
        { key: 'administracion-reportes', label: 'Reportes' },
        { key: 'administracion-logs', label: 'Logs' },
      ],
    },
    {
      label: 'data4n6',
      key: 'data4n6',
      icon: <DatabaseOutlined />,
      children: [
        { key: 'data4n6-dashboard', label: 'Dashboard' },
        { key: 'data4n6-analisis', label: 'Análisis' },
        { key: 'data4n6-config', label: 'Configuración' },
      ],
    },
    {
      label: 'Inventario',
      key: 'inventario',
      icon: <InboxOutlined />,
      children: [
        { key: 'inventario-listado', label: 'Listado Inventario' },
        { key: 'inventario-agregar', label: 'Agregar Artículo' },
        { key: 'inventario-buscar', label: 'Buscar' },
      ],
    },
    {
      label: 'Herramientas',
      key: 'herramientas',
      icon: <ToolOutlined />,
      children: [
        { key: 'herramientas-exportar', label: 'Exportar' },
        { key: 'herramientas-importar', label: 'Importar' },
        { key: 'herramientas-backup', label: 'Backup' },
      ],
    },
  ];

  const onClick: MenuProps['onClick'] = (e) => {
    setCurrent(e.key);

    if (onMenuSelect) {
      const keyParts = e.key.split('-');
      const menuId = keyParts[0];

      if (e.key === 'inventario-listado') {
        onMenuSelect('inventario', 'Listado Inventario');
      } else {
        onMenuSelect(menuId, e.domEvent.currentTarget.textContent || '');
      }
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', padding: '0 24px' }}>
      <Title level={3} style={{ margin: 0, marginRight: 24, color: '#1890ff' }}>
        data4n6
      </Title>
      <Menu
        onClick={onClick}
        selectedKeys={[current]}
        mode="horizontal"
        items={items}
        style={{ flex: 1, minWidth: 0 }}
      />
    </div>
  );
};

export default MenuHorizontal;

