      filters: [
        ...Array.from(new Set(articulos.map(item => item.marca))).map(marca => ({
          text: marca,
          value: marca,
        }))
      ],
      onFilter: (value, record) => record.marca === value,
    },
    {
      title: 'Modelo',
      dataIndex: 'modelo',
      key: 'modelo',
      sorter: (a, b) => a.modelo.localeCompare(b.modelo),
    },
    {
      title: 'Nº Serie',
      dataIndex: 'numeroSerie',
      key: 'numeroSerie',
      sorter: (a, b) => a.numeroSerie.localeCompare(b.numeroSerie),
