import React, { useState, useEffect } from 'react';
import { Table, Typography, Alert, Spin, Card, Tag } from 'antd';
import type { TableProps } from 'antd';
import { InboxOutlined } from '@ant-design/icons';

const { Title } = Typography;

interface Articulo {
  key: string;
  id: string;
  marca: string;
  modelo: string;
  numeroSerie: string;
  tipoMaterial: string;
}

const InventarioList: React.FC = () => {
  const [articulos, setArticulos] = useState<Articulo[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadInventario = async () => {
      try {
        const response = await fetch('/inventario.xml');
        if (!response.ok) {
          throw new Error('Error al cargar el archivo XML');
        }

        const xmlText = await response.text();
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlText, 'text/xml');

        const articulosElements = xmlDoc.getElementsByTagName('articulo');
        const articulosData: Articulo[] = [];

        for (let i = 0; i < articulosElements.length; i++) {
          const articulo = articulosElements[i];
          const id = articulo.getElementsByTagName('id')[0].textContent || '';
          articulosData.push({
            key: id,
            id: id,
            marca: articulo.getElementsByTagName('marca')[0].textContent || '',
            modelo: articulo.getElementsByTagName('modelo')[0].textContent || '',
            numeroSerie: articulo.getElementsByTagName('numeroSerie')[0].textContent || '',
            tipoMaterial: articulo.getElementsByTagName('tipoMaterial')[0].textContent || ''
          });
        }

        setArticulos(articulosData);
        setLoading(false);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Error desconocido');
        setLoading(false);
      }
    };

    loadInventario();
  }, []);

  const getTipoMaterialColor = (tipo: string): string => {
    const colors: { [key: string]: string } = {
      'Móvil': '#81C784',
      'Portátil': '#4CAF50',
      'Impresora': '#66BB6A',
      'Cámara': '#A5D6A7',
      'Consola': '#C8E6C9',
      'Tablet': '#81C784',
      'Switch': '#4CAF50',
      'Router': '#66BB6A',
      'Monitor': '#A5D6A7',
      'Torre': '#C8E6C9',
      'Ratón': '#81C784',
      'Teclado': '#4CAF50',
      'Auriculares': '#66BB6A',
      'default': '#4CAF50'
    };
    return colors[tipo] || colors['default'];
  };

  const columns: TableProps<Articulo>['columns'] = [
    {
      title: 'ID',
      dataIndex: 'id',
      key: 'id',
      width: 80,
      sorter: (a, b) => a.id.localeCompare(b.id),
      render: (text: string) => <strong>{text}</strong>,
    },
    {
      title: 'Marca',
      dataIndex: 'marca',
      key: 'marca',
      sorter: (a, b) => a.marca.localeCompare(b.marca),
      filters: [
        ...Array.from(new Set(articulos.map(item => item.marca))).map(marca => ({
          text: marca,
          value: marca,
        }))
      ],
      onFilter: (value, record) => record.marca === value,
    },
    {
      title: 'Modelo',
      dataIndex: 'modelo',
      key: 'modelo',
      sorter: (a, b) => a.modelo.localeCompare(b.modelo),
    },
    {
      title: 'Nº Serie',
      dataIndex: 'numeroSerie',
      key: 'numeroSerie',
      sorter: (a, b) => a.numeroSerie.localeCompare(b.numeroSerie),
    },
    {
      title: 'Tipo Material',
      dataIndex: 'tipoMaterial',
      key: 'tipoMaterial',
      sorter: (a, b) => a.tipoMaterial.localeCompare(b.tipoMaterial),
      filters: [
        ...Array.from(new Set(articulos.map(item => item.tipoMaterial))).map(tipo => ({
          text: tipo,
          value: tipo,
        }))
      ],
      onFilter: (value, record) => record.tipoMaterial === value,
      render: (tipo: string) => (
        <Tag 
          style={{ 
            backgroundColor: getTipoMaterialColor(tipo),
            color: '#1B5E20',
            border: '1px solid #4CAF50'
          }} 
          icon={<InboxOutlined />}
        >
          {tipo}
        </Tag>
      ),
    },
  ];

  if (loading) {
    return (
      <div style={{ textAlign: 'center', padding: '50px' }}>
        <Spin size="large" />
        <p style={{ marginTop: '16px' }}>Cargando inventario...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Alert
        message="Error"
        description={error}
        type="error"
        showIcon
        style={{ margin: '20px 0' }}
      />
    );
  }

  return (
    <Card>
      <Title level={2} style={{ marginBottom: '24px', textAlign: 'center' }}>
        <InboxOutlined /> Inventario de Artículos
      </Title>
      <Table
        columns={columns}
        dataSource={articulos}
        pagination={{
          total: articulos.length,
          pageSize: 10,
          showSizeChanger: true,
          showQuickJumper: true,
          showTotal: (total, range) =>
            `${range[0]}-${range[1]} de ${total} artículos`,
        }}
        scroll={{ x: 800 }}
        size="middle"
        bordered
        title={() => (
          <div style={{ textAlign: 'center' }}>
            <strong>Total de artículos: {articulos.length}</strong>
          </div>
        )}
      />
    </Card>
  );
};

export default InventarioList;

