import React, { useState } from 'react';
import { Layout, Typography, theme, ConfigProvider } from 'antd';
import { generate } from '@ant-design/colors';
import './App.css';
import InventarioList from './components/InventarioList';
import MenuHorizontal from './components/MenuHorizontal';

const { Header, Content } = Layout;
const { Title, Paragraph } = Typography;

type ViewType = 'inventario-list' | null;

// Crear paleta personalizada mezclando Green50 y Gray50
const customTheme = {
  algorithm: theme.defaultAlgorithm,
  token: {
    colorPrimary: '#4CAF50', // Verde material como primario
    colorBgContainer: '#F1F8E9', // Mezcla de Green50 y Gray50
    colorBgLayout: '#FAFAFA', // Gray50
    colorBgElevated: '#E8F5E8', // Green50
    colorBorder: '#C8E6C9', // Verde claro para bordes
    colorText: '#2E7D32', // Verde oscuro para texto
    colorTextSecondary: '#66BB6A', // Verde medio para texto secundario
    borderRadius: 8,
    fontFamily: '"Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
  },
  components: {
    Layout: {
      bodyBg: '#FAFAFA',
      headerBg: '#E8F5E8',
    },
    Menu: {
      itemBg: 'transparent',
      itemSelectedBg: '#C8E6C9',
      itemActiveBg: '#A5D6A7',
      itemHoverBg: '#DCEDC8',
    },
    Table: {
      headerBg: '#E8F5E8',
      rowHoverBg: '#F1F8E9',
    },
    Card: {
      headerBg: '#E8F5E8',
    },
  },
};

function App(): React.ReactElement {
  const [currentView, setCurrentView] = useState<ViewType>(null);

  const handleMenuSelect = (menuId: string, menuLabel: string): void => {
    console.log(`Menú seleccionado: ${menuId} - ${menuLabel}`);

    if (menuId === 'inventario' && menuLabel === 'Listado Inventario') {
      setCurrentView('inventario-list');
    } else {
      setCurrentView(null);
    }
  };

  const renderContent = (): React.ReactElement => {
    switch (currentView) {
      case 'inventario-list':
        return <InventarioList />;
      default:
        return (
          <div
            style={{
              background: '#F1F8E9',
              minHeight: '80vh',
              padding: 48,
              borderRadius: 8,
              textAlign: 'center',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
            }}
          >
            <Title level={2} style={{ color: '#2E7D32' }}>
              Bienvenido a data4n6
            </Title>
            <Paragraph style={{ fontSize: '18px', color: '#66BB6A' }}>
              Selecciona una opción del menú para comenzar
            </Paragraph>
          </div>
        );
    }
  };

  return (
    <ConfigProvider theme={customTheme}>
      <Layout style={{ minHeight: '100vh' }}>
        <Header style={{ padding: 0 }}>
          <MenuHorizontal onMenuSelect={handleMenuSelect} />
        </Header>
        <Content style={{ padding: '24px 50px' }}>
          {renderContent()}
        </Content>
      </Layout>
    </ConfigProvider>
  );
}

export default App;

