  /**
   * Elimina un artículo por su ID
   */
  delete(id: string): Promise<boolean>;

  /**
   * Busca artículos por marca
   */
  findByMarca(marca: string): Promise<ArticuloData[]>;

  /**
   * Busca artículos por tipo de material
   */
  findByTipoMaterial(tipoMaterial: string): Promise<ArticuloData[]>;
}

