import { PrismaClient } from '@prisma/client';
import { IInventarioRepository, ArticuloData } from './IInventarioRepository';

export class PrismaInventarioRepository implements IInventarioRepository {
  private prisma: PrismaClient;

  constructor() {
    this.prisma = new PrismaClient();
  }

  async getAll(): Promise<ArticuloData[]> {
    try {
      const articulos = await this.prisma.articulo.findMany({
        orderBy: {
          createdAt: 'desc'
        }
      });

      return articulos.map(articulo => ({
        id: articulo.id,
        marca: articulo.marca,
        modelo: articulo.modelo,
        numeroSerie: articulo.numeroSerie,
        tipoMaterial: articulo.tipoMaterial
      }));
    } catch (error) {
      console.error('Error fetching all articles:', error);
      throw error;
    }
  }

  async getById(id: string): Promise<ArticuloData | null> {
    try {
      const articulo = await this.prisma.articulo.findUnique({
        where: { id }
      });

      if (!articulo) return null;

      return {
        id: articulo.id,
        marca: articulo.marca,
        modelo: articulo.modelo,
        numeroSerie: articulo.numeroSerie,
        tipoMaterial: articulo.tipoMaterial
      };
    } catch (error) {
      console.error('Error fetching article by ID:', error);
      throw error;
    }
  }

  async create(articulo: Omit<ArticuloData, 'id'>): Promise<ArticuloData> {
    try {
      // Generar un ID único basado en timestamp
      const id = Date.now().toString();

      const newArticulo = await this.prisma.articulo.create({
        data: {
          id,
          marca: articulo.marca,
          modelo: articulo.modelo,
          numeroSerie: articulo.numeroSerie,
          tipoMaterial: articulo.tipoMaterial
        }
      });

      return {
        id: newArticulo.id,
        marca: newArticulo.marca,
        modelo: newArticulo.modelo,
        numeroSerie: newArticulo.numeroSerie,
        tipoMaterial: newArticulo.tipoMaterial
      };
    } catch (error) {
      console.error('Error creating article:', error);
      throw error;
    }
  }

  async update(id: string, data: Partial<ArticuloData>): Promise<ArticuloData | null> {
    try {
      const updatedArticulo = await this.prisma.articulo.update({
        where: { id },
        data: {
          ...(data.marca && { marca: data.marca }),
          ...(data.modelo && { modelo: data.modelo }),
          ...(data.numeroSerie && { numeroSerie: data.numeroSerie }),
          ...(data.tipoMaterial && { tipoMaterial: data.tipoMaterial })
        }
      });

      return {
        id: updatedArticulo.id,
        marca: updatedArticulo.marca,
        modelo: updatedArticulo.modelo,
        numeroSerie: updatedArticulo.numeroSerie,
        tipoMaterial: updatedArticulo.tipoMaterial
      };
    } catch (error) {
      console.error('Error updating article:', error);
      return null;
    }
  }

  async delete(id: string): Promise<boolean> {
    try {
      await this.prisma.articulo.delete({
        where: { id }
      });
      return true;
    } catch (error) {
      console.error('Error deleting article:', error);
      return false;
    }
  }

  async findByMarca(marca: string): Promise<ArticuloData[]> {
    try {
      const articulos = await this.prisma.articulo.findMany({
        where: {
          marca: {
            contains: marca,
            mode: 'insensitive'
          }
        },
        orderBy: {
          createdAt: 'desc'
        }
      });

      return articulos.map(articulo => ({
        id: articulo.id,
        marca: articulo.marca,
        modelo: articulo.modelo,
        numeroSerie: articulo.numeroSerie,
        tipoMaterial: articulo.tipoMaterial
      }));
    } catch (error) {
      console.error('Error finding articles by marca:', error);
      throw error;
    }
  }

  async findByTipoMaterial(tipoMaterial: string): Promise<ArticuloData[]> {
    try {
      const articulos = await this.prisma.articulo.findMany({
        where: {
          tipoMaterial: {
            contains: tipoMaterial,
            mode: 'insensitive'
          }
        },
        orderBy: {
          createdAt: 'desc'
        }
      });

      return articulos.map(articulo => ({
        id: articulo.id,
        marca: articulo.marca,
        modelo: articulo.modelo,
        numeroSerie: articulo.numeroSerie,
        tipoMaterial: articulo.tipoMaterial
      }));
    } catch (error) {
      console.error('Error finding articles by tipo material:', error);
      throw error;
    }
  }

  async disconnect(): Promise<void> {
    await this.prisma.$disconnect();
  }
}

