import { PrismaClient } from '@prisma/client';
import { XmlInventarioRepository } from '../repositories/XmlInventarioRepository';
import { PrismaInventarioRepository } from '../repositories/PrismaInventarioRepository';

export async function migrateXmlToDatabase(): Promise<void> {
  const xmlRepo = new XmlInventarioRepository();
  const dbRepo = new PrismaInventarioRepository();

  try {
    console.log('🔄 Starting XML to Database migration...');

    // Obtener todos los artículos del XML
    const articulos = await xmlRepo.getAll();
    console.log(`📦 Found ${articulos.length} articles in XML`);

    // Limpiar la base de datos antes de migrar
    const prisma = new PrismaClient();
    await prisma.articulo.deleteMany({});
    console.log('🧹 Database cleaned');

    // Migrar cada artículo a la base de datos
    let migratedCount = 0;
    for (const articulo of articulos) {
      try {
        await dbRepo.create({
          marca: articulo.marca,
          modelo: articulo.modelo,
          numeroSerie: articulo.numeroSerie,
          tipoMaterial: articulo.tipoMaterial
        });
        migratedCount++;
        console.log(`✅ Migrated: ${articulo.marca} ${articulo.modelo} (${migratedCount}/${articulos.length})`);
      } catch (error) {
        console.error(`❌ Error migrating article ${articulo.id}:`, error);
      }
    }

    console.log(`🎉 Migration completed! ${migratedCount} articles migrated successfully.`);

    // Verificar la migración
    const migratedArticles = await dbRepo.getAll();
    console.log(`✅ Verification: ${migratedArticles.length} articles found in database`);

    await prisma.$disconnect();

  } catch (error) {
    console.error('❌ Migration failed:', error);
    throw error;
  }
}

// Ejecutar la migración si se ejecuta directamente
if (require.main === module) {
  migrateXmlToDatabase()
    .then(() => {
      console.log('Migration completed successfully!');
      process.exit(0);
    })
    .catch((error) => {
      console.error('Migration failed:', error);
      process.exit(1);
    });
}

