import React, { useState, useEffect } from 'react';
import { Table, Typography, Alert, Spin, Card, Tag, Switch } from 'antd';
import type { TableProps } from 'antd';
import { InboxOutlined, DatabaseOutlined, FileOutlined } from '@ant-design/icons';
import { InventarioService } from '../services/InventarioService';
import type { ArticuloData } from '../repositories/IInventarioRepository';

const { Title } = Typography;

interface Articulo extends ArticuloData {
  key: string;
}

const InventarioList: React.FC = () => {
  const [articulos, setArticulos] = useState<Articulo[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [inventarioService] = useState(() => new InventarioService('database'));
  const [useDatabase, setUseDatabase] = useState<boolean>(true);

  const loadInventario = async () => {
    try {
      setLoading(true);
      setError(null);

      const articulosData = await inventarioService.getAllArticulos();

      const articulosWithKeys: Articulo[] = articulosData.map(articulo => ({
        ...articulo,
        key: articulo.id
      }));

      setArticulos(articulosWithKeys);
      setLoading(false);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Error desconocido');
      setLoading(false);
    }
  };

  const handleDataSourceChange = (checked: boolean) => {
    setUseDatabase(checked);
    inventarioService.switchDataSource(checked ? 'database' : 'xml');
    loadInventario();
  };

  useEffect(() => {
    loadInventario();
  }, []);

  const getTipoMaterialColor = (tipo: string): string => {
    const colors: { [key: string]: string } = {
      'Móvil': '#81C784',
      'Portátil': '#4CAF50',
      'Impresora': '#66BB6A',
      'Cámara': '#A5D6A7',
      'Consola': '#C8E6C9',
      'Tablet': '#81C784',
      'Switch': '#4CAF50',
      'Router': '#66BB6A',
      'Monitor': '#A5D6A7',
      'Torre': '#C8E6C9',
      'Ratón': '#81C784',
      'Teclado': '#4CAF50',
      'Auriculares': '#66BB6A',
      'default': '#4CAF50'
    };
    return colors[tipo] || colors['default'];
  };

  const columns: TableProps<Articulo>['columns'] = [
    {
      title: 'ID',
      dataIndex: 'id',
      key: 'id',
      width: 80,
      sorter: (a, b) => a.id.localeCompare(b.id),
      render: (text: string) => <strong>{text}</strong>,
    },
    {
      title: 'Marca',
      dataIndex: 'marca',
      key: 'marca',
      sorter: (a, b) => a.marca.localeCompare(b.marca),
      filters: [
        ...Array.from(new Set(articulos.map(item => item.marca))).map(marca => ({
          text: marca,
          value: marca,
        }))
      ],
      onFilter: (value, record) => record.marca === value,
    },
    {
      title: 'Modelo',
      dataIndex: 'modelo',
      key: 'modelo',
      sorter: (a, b) => a.modelo.localeCompare(b.modelo),
    },
    {
      title: 'Nº Serie',
      dataIndex: 'numeroSerie',
      key: 'numeroSerie',
      sorter: (a, b) => a.numeroSerie.localeCompare(b.numeroSerie),
    },
    {
      title: 'Tipo Material',
      dataIndex: 'tipoMaterial',
      key: 'tipoMaterial',
      sorter: (a, b) => a.tipoMaterial.localeCompare(b.tipoMaterial),
      filters: [
        ...Array.from(new Set(articulos.map(item => item.tipoMaterial))).map(tipo => ({
          text: tipo,
          value: tipo,
        }))
      ],
      onFilter: (value, record) => record.tipoMaterial === value,
      render: (tipo: string) => (
        <Tag 
          style={{ 
            backgroundColor: getTipoMaterialColor(tipo),
            color: '#1B5E20',
            border: '1px solid #4CAF50'
          }} 
          icon={<InboxOutlined />}
        >
          {tipo}
        </Tag>
      ),
    },
  ];

  if (loading) {
    return (
      <div style={{ textAlign: 'center', padding: '50px' }}>
        <Spin size="large" />
        <p style={{ marginTop: '16px' }}>Cargando inventario...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Alert
        message="Error"
        description={error}
        type="error"
        showIcon
        style={{ margin: '20px 0' }}
      />
    );
  }

  return (
    <Card style={{ backgroundColor: '#F1F8E9', border: '1px solid #C8E6C9' }}>
      <Title level={2} style={{ marginBottom: '24px', textAlign: 'center', color: '#2E7D32' }}>
        <InboxOutlined /> Inventario de Artículos
      </Title>
      <Table
        columns={columns}
        dataSource={articulos}
        pagination={{
          total: articulos.length,
          pageSize: 10,
          showSizeChanger: true,
          showQuickJumper: true,
          showTotal: (total, range) =>
            `${range[0]}-${range[1]} de ${total} artículos`,
        }}
        scroll={{ x: 800 }}
        size="middle"
        bordered
        title={() => (
          <div style={{ textAlign: 'center', color: '#2E7D32' }}>
            <strong>Total de artículos: {articulos.length}</strong>
          </div>
        )}
      />
    </Card>
  );
};

export default InventarioList;

