import { IInventarioRepository, ArticuloData } from '../repositories/IInventarioRepository';
import { XmlInventarioRepository } from '../repositories/XmlInventarioRepository';
import { PrismaInventarioRepository } from '../repositories/PrismaInventarioRepository';

export type DataSource = 'xml' | 'database';

export class InventarioServiceBrowser {
  private repository: IInventarioRepository;
  private currentDataSource: DataSource;

  constructor(dataSource: DataSource = 'database') {
    this.currentDataSource = dataSource;
    this.repository = this.createRepository(dataSource);
  }

  private createRepository(dataSource: DataSource): IInventarioRepository {
    switch (dataSource) {
      case 'xml':
        return new XmlInventarioRepository();
      case 'database':
        return new PrismaInventarioRepository();
      default:
        throw new Error(`Unsupported data source: ${dataSource}`);
    }
  }

  /**
   * Cambia la fuente de datos
   */
  switchDataSource(dataSource: DataSource): void {
    this.currentDataSource = dataSource;
    this.repository = this.createRepository(dataSource);
  }

  /**
   * Obtiene la fuente de datos actual
   */
  getCurrentDataSource(): DataSource {
    return this.currentDataSource;
  }

  /**
   * Obtiene todos los artículos
   */
  async getAllArticulos(): Promise<ArticuloData[]> {
    return this.repository.getAll();
  }

  /**
   * Obtiene un artículo por ID
   */
  async getArticuloById(id: string): Promise<ArticuloData | null> {
    return this.repository.getById(id);
  }

  /**
   * Crea un nuevo artículo
   */
  async createArticulo(articulo: Omit<ArticuloData, 'id'>): Promise<ArticuloData> {
    return this.repository.create(articulo);
  }

  /**
   * Actualiza un artículo
   */
  async updateArticulo(id: string, articulo: Partial<ArticuloData>): Promise<ArticuloData | null> {
    return this.repository.update(id, articulo);
  }

  /**
   * Elimina un artículo
   */
  async deleteArticulo(id: string): Promise<boolean> {
    return this.repository.delete(id);
  }

  /**
   * Busca artículos por marca
   */
  async findByMarca(marca: string): Promise<ArticuloData[]> {
    return this.repository.findByMarca(marca);
  }

  /**
   * Busca artículos por tipo de material
   */
  async findByTipoMaterial(tipoMaterial: string): Promise<ArticuloData[]> {
    return this.repository.findByTipoMaterial(tipoMaterial);
  }

  /**
   * Verifica si el repositorio actual soporta operaciones de escritura
   */
  supportsWriteOperations(): boolean {
    return this.currentDataSource === 'database';
  }
}

