import { IInventarioRepository, ArticuloData } from './IInventarioRepository';
import * as xml2js from 'xml2js';

export class XmlInventarioRepository implements IInventarioRepository {
  private xmlPath: string;

  constructor(xmlPath: string = '/inventario.xml') {
    this.xmlPath = xmlPath;
  }

  async getAll(): Promise<ArticuloData[]> {
    try {
      // En Node.js, usamos el sistema de archivos
      if (typeof window === 'undefined') {
        const fs = require('fs');
        const path = require('path');
        const xmlPath = path.join(process.cwd(), 'public', 'inventario.xml');
        const xmlText = fs.readFileSync(xmlPath, 'utf8');

        const parser = new xml2js.Parser();
        const result = await parser.parseStringPromise(xmlText);

        const articulosData: ArticuloData[] = [];

        if (result.inventario && result.inventario.articulo) {
          for (const articulo of result.inventario.articulo) {
            articulosData.push({
              id: articulo.id[0] || '',
              marca: articulo.marca[0] || '',
              modelo: articulo.modelo[0] || '',
