import * as xml2js from 'xml2js';

export class XmlInventarioRepository implements IInventarioRepository {
  private xmlPath: string;

  constructor(xmlPath: string = '/inventario.xml') {
    this.xmlPath = xmlPath;
  }

  async getAll(): Promise<ArticuloData[]> {
    try {
      // En Node.js, usamos el sistema de archivos
      if (typeof window === 'undefined') {
        const fs = require('fs');
        const path = require('path');
        const xmlPath = path.join(process.cwd(), 'public', 'inventario.xml');
        const xmlText = fs.readFileSync(xmlPath, 'utf8');

