import { IInventarioRepository, ArticuloData } from './IInventarioRepository';

export class XmlInventarioRepository implements IInventarioRepository {
  private xmlPath: string;

  constructor(xmlPath: string = '/inventario.xml') {
    this.xmlPath = xmlPath;
  }

  async getAll(): Promise<ArticuloData[]> {
    try {
      const response = await fetch(this.xmlPath);
      if (!response.ok) {
        throw new Error('Error al cargar el archivo XML');
      }

      const xmlText = await response.text();
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(xmlText, 'text/xml');

      const articulosElements = xmlDoc.getElementsByTagName('articulo');
      const articulosData: ArticuloData[] = [];

      for (let i = 0; i < articulosElements.length; i++) {
        const articulo = articulosElements[i];
        articulosData.push({
          id: articulo.getElementsByTagName('id')[0].textContent || '',
          marca: articulo.getElementsByTagName('marca')[0].textContent || '',
          modelo: articulo.getElementsByTagName('modelo')[0].textContent || '',
          numeroSerie: articulo.getElementsByTagName('numeroSerie')[0].textContent || '',
          tipoMaterial: articulo.getElementsByTagName('tipoMaterial')[0].textContent || ''
        });
      }

      return articulosData;
    } catch (error) {
      console.error('Error loading XML data:', error);
      throw error;
    }
  }

  async getById(id: string): Promise<ArticuloData | null> {
    const articulos = await this.getAll();
    return articulos.find(articulo => articulo.id === id) || null;
  }

  async create(articulo: Omit<ArticuloData, 'id'>): Promise<ArticuloData> {
    // Para XML, esta operación requeriría escribir el archivo
    // Por ahora, simulamos la creación con un ID generado
    const newArticulo: ArticuloData = {
      id: Date.now().toString(),
      ...articulo
    };

    throw new Error('Create operation not supported for XML repository. Please use database repository.');
  }

  async update(id: string, articulo: Partial<ArticuloData>): Promise<ArticuloData | null> {
    throw new Error('Update operation not supported for XML repository. Please use database repository.');
  }

  async delete(id: string): Promise<boolean> {
    throw new Error('Delete operation not supported for XML repository. Please use database repository.');
  }

  async findByMarca(marca: string): Promise<ArticuloData[]> {
    const articulos = await this.getAll();
    return articulos.filter(articulo => articulo.marca === marca);
  }

  async findByTipoMaterial(tipoMaterial: string): Promise<ArticuloData[]> {
    const articulos = await this.getAll();
    return articulos.filter(articulo => articulo.tipoMaterial === tipoMaterial);
  }
}

