def setBookmarkFieldCheckBoxLegacy(xml, bmName: str, value : bool) -> bool :

    strValue = "0"
    if value == True :
        strValue = "1"

    bok = xml.SearchForAttribute2(xml, "w:bookmarkStart", "w:name", bmName)
