class ACCIONES(StrEnum):
    ActionReplaceTextWithText   = "ActionReplaceTextWithText"
    ActionReplaceTextWithImage  = "ActionReplaceTextWithImage"
    ActionSetFormCheckbox       = "ActionSetFormCheckbox"
    ActionSetBookmarkFormCheckbox = "ActionSetBookmarkFormCheckbox"
    ActionSetBookmarkFormField = "ActionSetBookmarkFormField"

class Action :

    def __init__(self, name) :
        self.name = name

    def __str__(self):
        return f"Action({self.name})"

    def __repr__(self):
        return self.__str__()

class ActionReplaceTextWithText(Action) :

    def __init__(self, searchText=None, replaceText=None) :
        super().__init__(ACCIONES.ActionReplaceTextWithText)
        self.searchText = searchText
        self.replaceText = replaceText

    def setSearchText(self, searchText) :
        self.searchText = searchText

    def setReplaceText(self, replaceText) :
        self.replaceText = replaceText

    def __str__(self):
      return f'{{"action": "{self.name}", "searchText": "{self.searchText}", "replaceText": "{self.replaceText}"}}'


class ActionReplaceTextWithImage(Action) :

    def __init__(self, searchValue=None, imgId=None, width=None, height=None) :
        super().__init__(ACCIONES.ActionReplaceTextWithImage)
        self.searchValue = searchValue
        self.imgId = imgId
        self.width = width
        self.height = height

    def setSearchValue(self, searchValue) :
        self.searchValue = searchValue

    def setImgId(self, imgId) :
        self.imgId = imgId

    def setWidth(self, width : float) :
        self.width = width

    def setHeight(self, height: float) :
        self.height = height

    def __str__(self):
        return f"ReplaceTextWithImage('{self.searchValue}' → img_id='{self.imgId}', {self.width}x{self.height})"

