        self.actions : list = []
        self.images : dict = {}  # id -> Image
        self.values : dict = {}  # id -> Value

    def loadConfig(self, xml) :
        if xml.startswith("XML://"):
            return self.loadXML(xml[6:])
        elif xml.startswith("FILE://"):
            return self.loadXMLFile(xml[7:])
        else:
            return self.loadXML(xml)

    def loadXML(self, xml_string) :
        try:
            root = etree.fromstring(xml_string)
            return self._parseXML(root)
        except etree.XMLSyntaxError as e:
            print(f"Error parsing XML: {e}")
            return False

    def loadXMLFile(self, xmlFile) -> bool:
        try:
            tree = etree.parse(xmlFile)
            root = tree.getroot()
            return self._parseXML(root)
        except (etree.XMLSyntaxError, FileNotFoundError) as e:
            print(f"Error loading XML file: {e}")
            return False

    def _parseXML(self, root) -> bool:
        if root.tag != "ineoDoc":
            print("El fichero de configuración debe tener como nodo root ineoDoc")
            return False

        # Parse images
        images_node = root.find("images")
        if images_node is not None:
            for image_node in images_node.findall("image"):
                id = image_node.get("id")
                md5 = image_node.get("md5")
                path = image_node.text
                self.images[id] = Image(id, md5, path)

        # Parse values
        values_node = root.find("values")
        if values_node is not None:
            for value_node in values_node.findall("value"):
                id = value_node.get("id")
                content = value_node.text
                self.values[id] = Value(id, content)

        # Parse fileIn
        file_in_node = root.find("fileIn")
        if file_in_node is not None:
            bok, format, source = utils.format_source(file_in_node.text)
            if (bok == True) :
