    # Método alternativo usando XPath para parsing más eficiente
    def _parseXML_xpath(self, root) -> bool:
        """Versión alternativa usando XPath - más eficiente para XMLs grandes"""
        if root.tag != "ineoDoc":
            print("El fichero de configuración debe tener como nodo root ineoDoc")
            return False

        # Parse images usando XPath
        for img in root.xpath("//images/image"):
            id = img.get("id")
            md5 = img.get("md5")
            path = img.text
            self.images[id] = Image(id, md5, path)

        # Parse values usando XPath
        for val in root.xpath("//values/value"):
            id = val.get("id")
            content = val.text
            self.values[id] = Value(id, content)

        # Parse fileIn y fileOut
        file_in = root.xpath("//fileIn/text()")
        self.fileIn = file_in[0] if file_in else None

        file_out = root.xpath("//fileOut/text()")
        self.fileOut = file_out[0] if file_out else None

        # Parse actions
        for action in root.xpath("//actions/action"):
            action_name = action.get("name")
            for item in action.xpath("items/item"):
                if action_name == "replaceTextWithText":
                    search_value = item.xpath("searchValue/text()")
                    search_value = search_value[0] if search_value else None
                    value_id = item.xpath("value/@id")
                    value_id = value_id[0] if value_id else None
                    self.actions.append(ActionReplaceTextWithText(search_value, value_id))

                elif action_name == "replaceTextWithImage":
                    search_value = item.xpath("searchValue/text()")
                    search_value = search_value[0] if search_value else None
                    img_id = item.xpath("img/@id")
                    img_id = img_id[0] if img_id else None
                    width = item.xpath("width/text()")
                    width = float(width[0]) if width and width[0] else None
                    height = item.xpath("height/text()")
                    height = float(height[0]) if height and height[0] else None
                    self.actions.append(ActionReplaceTextWithImage(search_value, img_id, width, height))

                elif action_name == "setFormCheckbox":
                    search_value = item.xpath("searchValue/text()")
                    search_value = search_value[0] if search_value else None
                    value_id = item.xpath("value/@id")
                    value_id = value_id[0] if value_id else None
                    self.actions.append(ActionSetFormCheckbox(search_value, value_id))

        return True

import os

from docx import Document
from docx.shared import Inches
from docx.text.paragraph import Paragraph
from docx.oxml.ns import qn
from docx.oxml import OxmlElement

def replaceTextWithText(doc, action):

    searchText  = action.searchText
    replaceText = action.replaceText

    for section in doc.sections:
        process_part(doc, searchText, replaceText, None)
        process_part(section.header, searchText, replaceText, None)
        process_part(section.footer, searchText, replaceText, None)

def process_part(part, search_text, replace_text=None, image_path=None, width=None, height=None):

    process_paragraphs(part.paragraphs, search_text, replace_text, image_path, width, height)
    process_tables(part.tables, search_text, replace_text, image_path, width, height)
    process_textboxes(part, search_text, replace_text, image_path, width, height)

def process_paragraphs(paragraphs, search_text, replace_text=None, image_path=None, width=None, height=None):

    for paragraph in paragraphs:
        replace_in_paragraph(paragraph, search_text, replace_text, image_path, width, height)

def process_tables(tables, search_text, replace_text=None, image_path=None, width=None, height=None):
    for table in tables:
        for row in table.rows:
            for cell in row.cells:
                process_paragraphs(cell.paragraphs, search_text, replace_text, image_path, width, height)
                process_tables(cell.tables, search_text, replace_text, image_path, width, height)

def process_textboxes(part, search_text, replace_text=None, image_path=None, width=None, height=None):

    textboxes = part._element.xpath('.//w:txbxContent')
    for tbx in textboxes:
        paras = [p for p in tbx.iter() if p.tag == qn('w:p')]
        for p in paras:
            try:
                para = paragraph_from_xml(p, part)
                replace_in_paragraph(para, search_text, replace_text, image_path, width, height)
            except Exception as e:
                print(f"Error procesando textbox: {e}")

def paragraph_from_xml(p, part):

    from docx.text.paragraph import Paragraph
    return Paragraph(p, part)

def replace_in_paragraph(paragraph, search_text, replace_text=None, image_path=None, width=None, height=None):


    full_text = ''.join(run.text for run in paragraph.runs)

    bokCoincidencia = "Hay coincidencia"
    if search_text not in full_text:
        bokCoincidencia = "No hay coincidencia"
        return  # No hay coincidencia, saltamos

    start_indices = []
    start = 0
    while True:
        idx = full_text.find(search_text, start)
        if idx == -1:
            break
        start_indices.append(idx)
        start = idx + len(search_text)

    if not start_indices:
        return

    # Limpiamos los runs existentes y reconstruimos el párrafo
    runs_to_keep = []
    current_pos = 0

    for start_idx in start_indices:
        # Parte antes del marcador
        before = full_text[current_pos:start_idx]
        if before:
            runs_to_keep.append({"text": before})
        if replace_text is not None:
            runs_to_keep.append({"text": replace_text})

        current_pos = start_idx + len(search_text)

    # Agregar lo que queda después del último match
    after = full_text[current_pos:]
    if after:
        runs_to_keep.append({"text": after})

    # Eliminar todos los runs antiguos
    for run in paragraph.runs:
        run.text = ""

    # Reconstruir el párrafo con los nuevos runs
    for item in runs_to_keep:
        if "text" in item:
            paragraph.add_run(item["text"])
        elif "image" in item:
            run = paragraph.add_run()
            run.add_picture(
                item["image"],
                width=item["width"],
                height=item["height"]
            )

