def setBookmarkFieldCheckBox(xml, bmName: str, value : bool, legacy : bool = True) -> bool :
    if (legacy) :
        setBookmarkFieldCheckBoxLegacy(xml, bmName, value)
    return True

def setBookmarkFieldCheckBoxLegacy(xml, bmName: str, value : bool) -> bool :

    strValue = "0"
    if value == True :
        strValue = "1"

    bok = xml.SearchForAttribute2(xml, "w:bookmarkStart", "w:name", bmName)
    if bok == False :
        logger.error(f"Bookmark con nombre {bmName} no encontrado.")
        return False
    logger.info(f"Bookmark con nombre {bmName} encontrado.")

    bok = False
    while True :
        if xml.Tag == "w:bookmarkEnd":
            break
        if not xml.NextSibling2():
            break
        if xml.SearchAllForContent(xml, "FORMCHECKBOX") :
            bok = True
            break

    if bok == True:
        xml.GetRoot2()
        sbState = chilkat2.StringBuilder()
        while xml.NextInTraversal2(sbState) != False :
            if xml.Tag == "w:r" :
                if xml.ChilkatPath("w:fldChar|w:ffData|w:name|(w:val)") == bmName:
                    xml.UpdateAttrAt("w:fldChar|w:ffData|w:checkBox|w:default", True, "w:val", strValue)
                    logger.info(f"Bookmark con nombre {bmName} procesado. Se establece valor a : {value}")
        return True
    else :
        xml.GetRoot2()
        logger.error(f"Bookmark con nombre {bmName} no es de tipo FORMCHECKBOX. No se procesa.")
        return False
