from enum import StrEnum
import chilkat2
from docx import Document

class ACCIONES(StrEnum):
    ActionReplaceTextWithText   = "ActionReplaceTextWithText"
    ActionReplaceTextWithImage  = "ActionReplaceTextWithImage"
    ActionSetFormCheckbox       = "ActionSetFormCheckbox"

class Action :

    def __init__(self, name) :
        self.name = name

class ActionReplaceTextWithText(Action) :

    def __init__(self, imgIn, searchValue, width, height) :
        super.name = ACCIONES.ActionReplaceTextWithText
        self.imgIn = None
        self.searchValue = None

    def setImgIn(self, imgIn) :
        self.imgIn = imgIn

    def setSearchValue(self, searchValue) :
        self.searchValue = searchValue

    def setWidth(self, width : float) :
        self.width = width

    def setHeight(self, height: float) :
        self.height = height

class ActionReplaceTextWithImage(Action) :

    def __init__(self, searchValue, value) :
        super.name = ACCIONES.ActionReplaceTextWithImage
        self.searchValue = None
        self.value = None

    def setSearchValue(self, searchValue) :
        self.searchValue = searchValue

    def setValue(self, value : str) :
        self.value = value

class ActionSetFormCheckbox(Action) : 

    def __init__(self, searchValue, value) :
        super.name = ACCIONES.ActionSetFormCheckbox
        self.searchValue = None
        self.value = None

    def setSearchValue(self, searchValue) :
        self.searchValue = searchValue

    def setValue(self, value : str) :
        self.value = value

class Config :

    doc : Document = None
    fileOut = None
    actions : list = []

    def loadConfig(self, xml) :
        if  xml[len("XML://"):] == "XML://" :
            pass
        if xml[len("FILE://"):] == "FILE://" :
            pass

    def loadXML(self, xml) :
        pass

    def loadXMLFile(self, xmlFile) -> bool:
        xml = chilkat2.Xml()
        bok = xml.LoadXmlFile(xmlFile)
        if (bok) :
            if xml.Tag != "ineoDoc" :
                print ("El fichero de configuración debe tener como nodo root ineoDoc")
                return False

