class Image:
    def __init__(self, id, md5, path):
        self.id = id
        self.md5 = md5
        self.path = path

    def __str__(self):
        path_type = "FILE" if self.path.startswith("FILE://") else "BASE64"
        return f"Image(id={self.id}, type={path_type}, md5={self.md5[:8]}...)"

    def __repr__(self):
        return self.__str__()

class Value:
    def __init__(self, id, content):
        self.id = id
        self.content = content

    def __str__(self):
        return f"Value(id={self.id}, content='{self.content}')"

    def __repr__(self):
        return self.__str__()

class Config :

    def __init__(self):
        self.doc : Document = None
        self.fileInData = None
        self.fileOutData = None
        self.fileInFormat = None
        self.fileOutFormat = None
        self.actions : list = []
        self.images : dict = {}  # id -> Image
        self.values : dict = {}  # id -> Value

    def loadConfig(self, xml) :
        if xml.startswith("XML://"):
            return self.loadXML(xml[6:])
        elif xml.startswith("FILE://"):
            return self.loadXMLFile(xml[7:])
        else:
            return self.loadXML(xml)

    def loadXML(self, xml_string) :
        try:
            root = etree.fromstring(xml_string)
            return self._parseXML(root)
        except etree.XMLSyntaxError as e:
            print(f"Error parsing XML: {e}")
            return False

    def loadXMLFile(self, xmlFile) -> bool:
        try:
            tree = etree.parse(xmlFile)
            root = tree.getroot()
            return self._parseXML(root)
        except (etree.XMLSyntaxError, FileNotFoundError) as e:
            print(f"Error loading XML file: {e}")
            return False

    def _parseXML(self, root) -> bool:
        if root.tag != "ineoDoc":
            print("El fichero de configuración debe tener como nodo root ineoDoc")
            return False

        # Parse images
        images_node = root.find("images")
        if images_node is not None:
            for image_node in images_node.findall("image"):
                id = image_node.get("id")
                md5 = image_node.get("md5")
                path = image_node.text
                self.images[id] = Image(id, md5, path)

        # Parse values
        values_node = root.find("values")
        if values_node is not None:
            for value_node in values_node.findall("value"):
                id = value_node.get("id")
                content = value_node.text
                self.values[id] = Value(id, content)

        # Parse fileIn
        file_in_node = root.find("fileIn")
        if file_in_node is not None:
            bok, format, source = utils.format_source(file_in_node.text)
            if (bok == True) :
                self.fileInData = source
                self.fileInFormat = format

        # Parse fileOut    
        file_out_node = root.find("fileOut")
        if file_out_node is not None:
            bok, format, source = utils.format_source(file_out_node.text)
            if (bok == True) :
                self.fileOutData = source
                self.fileOutFormat = format

        # Parse actions
        actions_node = root.find("actions")
        if actions_node is not None:
            for action_node in actions_node.findall("action"):
                self._parseAction(action_node)

        return True

