from docx import Document
from docx.shared import Inches
from docx.text.paragraph import Paragraph
from docx.oxml.ns import qn
from docx.oxml import OxmlElement

def fx_replaceTextWithText(doc, action):

    print ("replaceTextWithText")

    if action is not isinstance(ActionReplaceTextWithText) :
        return

    for section in doc.sections:
        process_part(doc, action)               # Body del documento
        process_part(section.header, action)    # Headers
        process_part(section.footer, action)    # Footers

def process_part(part, action):
    process_paragraphs(part.paragraphs, action) # Párrafos
    process_tables(part.tables, action)         # Tablas
    process_textboxes(part, action)             # TextBoxes

def process_paragraphs(paragraphs, action):
    for paragraph in paragraphs:
        replace_in_paragraph(paragraph, action)

def process_tables(tables, action):
    label = action.label
    text = action.text

    for table in tables:
        for row in table.rows:
            for cell in row.cells:
                process_paragraphs(cell.paragraphs, label, text)
                process_tables(cell.tables, label, text)

def process_textboxes(part, action):
    label = action.Label
    text = action.text

    textboxes = part._element.xpath('.//w:txbxContent')
    for tbx in textboxes:
        paras = [p for p in tbx.iter() if p.tag == qn('w:p')]
        for p in paras:
            try:
                para = paragraph_from_xml(p, part)
                replace_in_paragraph(para, label, text)
            except Exception as e:
                print(f"Error procesando textbox: {e}")

def paragraph_from_xml(p, part):

    from docx.text.paragraph import Paragraph
    return Paragraph(p, part)

def replace_in_paragraph(paragraph, action):
    label = action.label
    text = action.text
    full_text = ''.join(run.text for run in paragraph.runs)

    if label not in full_text:
        return  # No hay coincidencia, saltamos

    start_indices = []
    start = 0
    while True:
        idx = full_text.find(label, start)
        if idx == -1:
            break
        start_indices.append(idx)
        start = idx + len(label)

    if not start_indices:
        return

    # Limpiamos los runs existentes y reconstruimos el párrafo
    runs_to_keep = []
    current_pos = 0

    for start_idx in start_indices:
        # Parte antes del marcador
        before = full_text[current_pos:start_idx]
        if before:
            runs_to_keep.append({"text": before})
        if text is not None:
            runs_to_keep.append({"text": text})

        current_pos = start_idx + len(text)

    # Agregar lo que queda después del último match
    after = full_text[current_pos:]
    if after:
        runs_to_keep.append({"text": after})

    # Eliminar todos los runs antiguos
    for run in paragraph.runs:
        run.text = ""

    # Reconstruir el párrafo con los nuevos runs
    for item in runs_to_keep:
        if "text" in item:
            paragraph.add_run(item["text"])
        elif "image" in item:
            run = paragraph.add_run()
            run.add_picture(
                item["image"],
                width=item["width"],
                height=item["height"]
            )

